create or replace view DB_IAW_PROD_DWH.CLONE_INF_SHARED_BDV.VW_CLIENT_CONTRACT(
	HK_HUB_CONTRACT,
	HK_HUB_PARTY_ROLE_ACCOUNT_HOLDER,
	A_C_CLIENT,
	MD_START_DT,
	MD_ACTIVE
) as 
SELECT  DISTINCT 
