,SHA1(UPPER(CONCAT(COALESCE(TRIM(HC.MD_SRC_SYSTEM), '#NULL#'), '|' , COALESCE(TRIM(SC.A_C_CLIENT), '#NULL#')))) AS HK_HUB_PARTY_ROLE_ACCOUNT_HOLDER
,A_C_CLIENT
,SC.MD_START_DT 
