var res = st.execute();
var stUpdStatus;
var resUpdStatus;
var InsCmd = ""SELECT DISTINCT JOB_NAME FROM TOOLS.AUDIT_JOBS_EXECUTIONS aje WHERE aje.JOB_NAME =''"" + JOB_NAME + ""'' AND aje.DATA_START_DT = ''"" + DATA_START_DT +""''"";
