            cmdUpdStatus += ""''IN PROGRESS'', JOB_ACTION_RESULT=''SUCCESS'',  JOB_STATUS_DESC = COALESCE(JOB_STATUS_DESC,'''') || ''"" + JOB_AUDIT_ID + ""('' || CURRENT_TIMESTAMP() || '') : Job restart. ''"" + cmdUpdStatusWhereClause; 
