where MD_START_DT <= TO_DATE(''||IO_START_DATE||'',''''YYYY-MM-DD'''') group by HK_HUB ) as dtls 
on HK_HUB = dtls_hk_HUB and MD_START_DT =  max_start_dt where MD_ACTIVE = ''''A''''), 
