CREATE OR REPLACE FUNCTION DB_IAW_PROD_DWH.SHARED_BDV.EXTRACT_ACCOUNT_RISK_IVTOBJCDE_PCT(""P_ATTRIBUTE"" VARCHAR(15), ""SUBSTRING_START_POSITION"" NUMBER(38,0), ""POSITION_OCCUR"" NUMBER(38,0))
RETURNS NUMBER(38,0)
LANGUAGE SQL
