		MAX(Sat_Link_Investment_MD_EXTRACT_DT) as o_MAX_MD_EXTRACT_DT,
		SUM(case when SAT_LINK_INVESTMENT_AUA is null then ''''0'''' else SAT_LINK_INVESTMENT_AUA end) as o_Account_SUM_AUA
	from
