	,rdv.TRANSACTION_ID	
	,rdv.UNIVERIS_CLIENT_ID	
	,bdv02.MASTER_CODE	
	,rdv.UNIVERIS_PLAN_ID
	,rdv.INVESTMENT_PRODUCT_ID	
	,bdv2.PLN_MNEM	
	,trdv.TRADE_DT	
