			FROM DB_IAW_''||ENV||''_DWH.TRANSACTIONS_RDV.SAT_LINK_FINANCIAL_TRANSACTION_RR_IAS_UNIVERIS WHERE MD_START_DT = TO_DATE(''||i_DATA_START_DATE||'', ''''YYYY-MM-DD''''))trdv
