							PARTITION BY HK_HUB ORDER BY MD_START_DT DESC
							) RN4
					FROM DB_IAW_''||ENV||''_DWH.SHARED_RDV.SAT_INVESTMENT_PRODUCT_TYPE_IAS_UNIVERIS
