		DECODE(TRUE, CONTRACT.A_C_CLIENT IS NULL ,'0',SHA1(UPPER(CONCAT(COALESCE(TRIM(LT.MD_SRC_SYSTEM), '#NULL#'), '|' , COALESCE(TRIM(CONTRACT.A_C_CLIENT), '#NULL#')))))  AS HK_HUB_PARTY_ROLE_ACCOUNT_HOLDER, 
