		SLT.TI_BASIS_POINT AS TI_BASIS_POINT, 
		SLT.TI_FACE_VALUE AS TI_FACE_VALUE, 
		SLT.TI_SYMBOL AS TI_SYMBOL, 
		COMM_SHARE.COMMISSIONPCT AS COMMISSIONPCT, 
